//React
import React, {Component} from 'react'

//Style
import '../style/inscription.css'

class Inscription extends Component {
    state = {
        formulaire : {
            username  : '',
            firstname : '',
            lastname : '',
            gender : '',
            email : '',
            mailValidation : '',
            password : '',

        }

    }

    handleChange = (event) => {
        // let recupClass = event.target.className.split(' ')
        // let recupState = recupClass[1]
        // const formulaire = {...this.state.formulaire}
        // const list = Object.keys(formulaire)

        // for (let i = 0; i < list.length; i++) {
        //     if (list[i] == recupState) {
        //         formulaire[recupState] = event.target.value
        //     }
        // }
        // this.setState({formulaire : formulaire})

        this.setState({[event.target.id] : event.target.value})
    }    



    render () {
        return (
            <div className="container">
                <h2 className="title_form">Inscription</h2>
                <form>
                    <div className="form-group">
                        <label htmlFor="username">Pseudo :</label>
                        <input
                            value={this.state.username}
                            onChange={this.handleChange}
                            placeholder="Username"
                            id="username" 
                            type="text" 
                            className="form-control username" 
                            required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="firstname">Prénom :</label>
                        <input
                            value={this.state.firstname}
                            onChange={this.handleChange} 
                            id="firstname" 
                            type="text" 
                            className="form-control firstname" 
                            required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="lastname">Nom :</label>
                        <input id="lastname" type="text" className="form-control" required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="gender">Genre :</label>
                        <select id="gender" className="form-control">
                            <option value="masculin">Masculin</option>
                            <option value="feminin">Féminin</option>
                            <option value="gay">Gay</option>
                            <option value="non_binary">Non-binaire</option>
                        </select>
                    </div>
                    <div className="form-group">
                        <label htmlFor="email">Mail :</label>
                        <input id="email" type="mail" className="form-control" required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="mailValidation">Confirmer votre mail :</label>
                        <input id="mailValidation" type="mail" className="form-control" required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password">Mot de passe :</label>
                        <input id="password" type="password" className="form-control" required />
                    </div>
                    <div className="form-group">
                        <label htmlFor="password_confirm">Confirmer votre mot de passe :</label>
                        <input id="password_confirm" type="password" className="form-control" required />
                    </div>
                    <div className="boutton-form">
                        <input className="btn btn-info btn-lg" type="submit" value="S'inscrire"/>
                    </div>
                </form>
            </div>
        )
    }
}

export default Inscription
